import org.junit.Test;
import org.junit.runner.RunWith;
import org.junit.runners.Parameterized;

import java.util.Arrays;
import java.util.Collection;

import static org.junit.Assert.assertEquals;

@RunWith(Parameterized.class)
public class Day6Test {

    @Parameterized.Parameters
    public static Collection<Object[]> data() {
        return Arrays.asList(new Object[][]{
                {"bvwbjplbgvbhsrlpgdmjqwftvncz", 5, 23},
                {"nppdvjthqldpwncqszvftbrmjlhg", 6, 23},
                {"mjqjpqmgbljsphdztnvjfqwrcgsmlb", 7, 19},
                {"nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 10, 29},
                {"zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 11, 26},
                {"",
                        1892, 2313}
        });
    }

    private final String input;
    private final int startOfPacketMarker;
    private final int startOfMessageMarker;

    public Day6Test(String input, int startOfPacketMarker, int startOfMessageMarker){
        this.input = input;
        this.startOfPacketMarker = startOfPacketMarker;
        this.startOfMessageMarker = startOfMessageMarker;
    }

    @Test
    public void findStartOfPacketMarker(){
        assertEquals(this.startOfPacketMarker, CommunicationDevice.findStartOfMarker(this.input, 4));
    }

    @Test
    public void findStartOfMessageMarker(){
        assertEquals(this.startOfMessageMarker, CommunicationDevice.findStartOfMarker(this.input, 14));
    }
}
